export class Widget {
    top: number;
    left: number;
    bottom: number;
    right: number;
    color: string;

    constructor(pTop: number, pLeft: number, pBottom: number, pRight: number, pColor: string) {
        this.top = pTop;
        this.left = pLeft;
        this.bottom = pBottom;
        this.right = pRight;
        this.color = pColor;
    }
}
